mapper.select:
SELECT * FROM user


      AND userID = #{userID}


        AND userName = #{userName}


        AND userEmail = #{userEmail}


        AND userPhone = #{userPhone}


        AND userQQ = #{userQQ}


        AND userSex = #{userSex}


        AND userLastLoginIP = #{userLastLoginIP}



    LIMIT #{offset},#{limit}

SELECT count(1) FROM user


        AND userID = #{userID}


        AND userName = #{userName}


        AND userEmail = #{userEmail}


        AND userPhone = #{userPhone}


        AND userQQ = #{userQQ}


        AND userSex = #{userSex}


        AND userLastLoginIP = #{userLastLoginIP}

mapper.update:
UPDATE user
userName = #{userName}, userPWD = #{userPWD}, userEmail = #{userEmail}, userPhone = #{userPhone}, userQQ = #{userQQ}, userSex = #{userSex}, userLastLoginIP = #{userLastLoginIP},
WHERE userID = #{userID}
